    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Agregar procedimiento a la tabla
        String procedimiento = jTextField1.getText();
        if (!procedimiento.isEmpty()) {
            Object[] fila = new Object[2];
            fila[0] = modelo.getRowCount() + 1;
            fila[1] = procedimiento;
            modelo.addRow(fila);
            jTextField1.setText("");
        } else {
            JOptionPane.showMessageDialog(this, "Ingrese un procedimiento vÃ¡lido", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Eliminar procedimiento de la tabla
        int filaSeleccionada = tblTabla.getSelectedRow();
        if (filaSeleccionada >= 0) {
            modelo.removeRow(filaSeleccionada);
        } else {
            JOptionPane.showMessageDialog(this, "Seleccione un procedimiento para eliminar", "Error", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton2ActionPerformed

